INSERT INTO `cwcol_colunistas` (`person_id`, `apelido`, `alias`, `foto`, `resumo`, `bio`, `isactive`, `created`, `createdby`, `modified`, `modifiedby`) VALUES
	(1, 'André Luis', 'andre-luis', NULL, 'Idealizador do portal. Formado em marketing com MBA em Comunicação e Marketing nas Mídias Digitais.', '<p>Idealizador do portal. Formado em marketing com MBA em Comunica&ccedil;&atilde;o e Marketing nas M&iacute;dias Digitais.</p>\r\n', 'Y', '2016-06-14 08:39:38', NULL, '2016-09-25 18:25:13', NULL),
	(60, 'André', 'andre', NULL, 'erw', '<p>ere</p>\r\n', 'Y', '2016-06-04 10:12:01', NULL, '2016-09-18 20:52:24', NULL),
	(61, 'Eduardo Junior', 'eduardo', NULL, 'Apaixonado por informática, estudante de Análise e Desenvolvimento de Sistemas, Web Designer entusiasta, Co Fundador do portal CNW, Coordenador do caderno de TI e colunista. É assumidamente um dos maiores fãs de Star Wars dessa e de outras galáxias.', 'Nasci em 1978 em uma fam&iacute;lia tipicamente carioca, e segundo meus pais, desde pequeno eu sempre quis entender como funcionam as coisas, daquele tipo que desmontava o r&aacute;dio da av&oacute; pra ver como era por dentro.<br />\r\n<br />\r\nSempre tive fasc&iacute;nio por tecnologia, era louco de vontade de fazer aqueles cursos de eletr&ocirc;nica por correspond&ecirc;ncia do Instituto Nacional Brasileiro, daqueles que vinham em an&uacute;ncios nos gibis que eu lia, e em 1988 tive acesso ao meu primeiro PC, um maravilhoso e potente IBM XT 286 com invej&aacute;veis 256Kb de mem&oacute;ria RAM e disquetes de 5&frac14;&quot; de 1.2 MB.<br />\r\n<br />\r\nNaquele momento, nascia uma paix&atilde;o avassaladora pela inform&aacute;tica.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\nOs anos foram passando e minha paix&atilde;o crescendo, fiz os melhores cursos da &eacute;poca, Office 95, Windows 95, MSDOS, Frontpage.&nbsp;Mas eu ainda achava pouco, sonhava em mergulhar mais fundo no mundo tecnol&oacute;gico, e em 2001, entrei para o Curso T&eacute;cnico P&oacute;s M&eacute;dio da FAETEC em Tr&ecirc;s Rios<strong> </strong>e encontrei o para&iacute;so.<br />\r\n<br />\r\nDeliciosas m&aacute;quinas me esperavam para ser abertas e desmontadas, aprendi l&oacute;gica de programa&ccedil;&atilde;o, algoritmo e o portugol no bom e velho Turbo Pascal, passando depois para o Delphi. Ali criei la&ccedil;os fortes com as redes de computadores (que at&eacute; hoje s&atilde;o como uma v&aacute;lvula de escape para mim).<br />\r\n<br />\r\nApaixonado pelo desenvolvimento web, me lancei nos bra&ccedil;os do HTML, Dreamweaver, Flash. Essa paix&atilde;o crescia cada vez mais e acabei seguindo pelos caminhos do PHP, MySql e da plataforma Wordpress. &nbsp;<br />\r\n<br />\r\nConsumido pelo desejo de mais conhecimento, me matriculei no curso de Analise e Desenvolvimento de Sistemas na Est&aacute;cio.<br />\r\n<br />\r\nTrabalhei em empresas que contribu&iacute;ram bastante com meus conhecimentos t&eacute;cnicos, e foi numa dessas empresas que conheci o Andr&eacute;, o Carlos e o Marco Aur&eacute;lio, e &eacute; gra&ccedil;as a esse encontro e muita troca de ideias que hoje escrevo essa biografia para o Crescer na Web, que tem como principal objetivo, saciar a fome de conhecimento de apaixonados como eu, seja na &aacute;rea de Tecnologia como em qualquer outra &aacute;rea de conhecimento.<br />\r\n<br />\r\nEspero contribuir com o melhor e junto com cada um de voc&ecirc;s, aprender cada dia um pouco mais e Crescer na Web.<br />\r\n<br />\r\n&nbsp;', 'Y', '2016-06-04 10:14:09', NULL, '2016-10-25 14:44:30', NULL),
	(62, 'Marco Aurélio', 'marco', NULL, 'Bacharel em Sistemas de Informação pela Faculdade Metodista Granbery, desenvolvedor de aplicações web/mobile, entusiasta do software livre e de sistemas linux e profundo amante da tecnologia e da cultura Geek.', '<p>Bacharel em Sistemas de Informa&ccedil;&atilde;o pela Faculdade Metodista Granbery, desenvolvedor de aplica&ccedil;&otilde;es web/mobile, entusiasta do software livre e de sistemas linux e profundo amante da tecnologia e da cultura Geek.</p>\r\n', 'Y', '2016-06-04 10:15:16', NULL, '2016-08-16 14:56:40', NULL),
	(63, 'Carlos Vinicius', 'carlos-vinicius', NULL, 'Co-Fundador do Portal Crescer na Web e Coordenador do Caderno de Empreendedorismo, Logística e Processos gerenciais. Formado em Administração de Empresas na Faculdade do Sudeste Mineiro e Técnico em Logística e Informática.', 'Atualmente minha vida esta pautada em realiza&ccedil;&otilde;es profissionais na ADMINISTRA&Ccedil;&Atilde;O e na LOG&Iacute;STICA. O lado profissional come&ccedil;ou a partir de um Curso T&eacute;cnico em Inform&aacute;tica, onde encontrei al&eacute;m de conhecimento, bons amigos que est&atilde;o ao meu lado at&eacute; hoje e onde desenvolvi a mentalidade que o conhecimento deve ser compartilhado.<br />\r\nO primeiro contato com a Log&iacute;stica foi atrav&eacute;s de est&aacute;gio em inform&aacute;tica, onde aprendi com uma pessoa que todo processo deveria ser visto de forma SIST&Ecirc;MICA. Conclu&iacute;do o curso de T&eacute;cnico em Inform&aacute;tica, comecei lecionar em cursos de inform&aacute;tica, onde trabalhei por mais de 8 anos, mas ainda sentia falta da din&acirc;mica da log&iacute;stica e suas estrat&eacute;gias, ent&atilde;o, aprovado no vestibular do Instituto Federal de Educa&ccedil;&atilde;o e Tecnologia(IFET) em T&eacute;cnico em Transporte e Transito (Log&iacute;stica), dai voltei atuar na &aacute;rea e estudar t&eacute;cnicas inovadoras da GEST&Atilde;O DA CADEIA LOG&Iacute;STICA. Nesse ponto a curiosidade j&aacute; estava agu&ccedil;ada entre Inform&aacute;tica, Log&iacute;stica e Gest&atilde;o. Segundo um dos melhores professores que j&aacute; tive,&nbsp; cada curso representa uma &ldquo;Chave&rdquo; e que a mesma lhe dar&aacute; oportunidades se est&aacute; preparado.<br />\r\nEnt&atilde;o, fui cursar Administra&ccedil;&atilde;o de empresas na Faculdade do Suldeste Mineiro para aperfei&ccedil;oar a gest&atilde;o, outro mundo, novas perspectivas, Fiquei maravilhado parecia que estava come&ccedil;ando a acha respostas, inspirado e em meio a Faculdade ainda deu tempo para Cursar Log&iacute;stica em Maca&eacute; no PROMINP.<br />\r\nMas, o que adianta o conhecimento se o mesmo n&atilde;o for compartilhado, para que possamos crescer como indiv&iacute;duos e profissionalmente, acredito que devemos compartilhar experi&ecirc;ncias.<br />\r\nEnt&atilde;o, Crescer na Web disseminando conhecimento, um projeto pra vida!<br />\r\n<br />\r\n<br />\r\n&nbsp;', 'Y', '2016-06-05 18:31:12', NULL, '2016-10-22 13:01:19', NULL);
