INSERT INTO `cw_postagens` (`id`, `titulo`, `slug`, `resumo`, `texto`, `createdby`, `modifiedby`, `isdeleted`, `created`, `modified`, `isactive`, `cat_conteudo_id`) VALUES
	(1, 'Nossa empresa', NULL, NULL, 'Uma hist&oacute;ria de Sucesso!<br />\r\n<strong>A PAVAN</strong>&nbsp;iniciou suas atividades em 1957, com abertura de sua primeira loja&nbsp;na Rua Batista de Oliveira, 307. A&nbsp;expans&atilde;o se deu com a inaugura&ccedil;&atilde;o da loja&nbsp;na Rua Halfeld, 259, esquina com a Rua Paulo de Frontin, na &eacute;poca, um dos pontos mais ousados da cidade.<br />\r\nA empresa cresceu, ampliou o n&ordm; de lojas e se tornou refer&ecirc;ncia no seguimento de material de constru&ccedil;&atilde;o, em Juiz de Fora e regi&atilde;o.&nbsp;<br />\r\nEst&aacute;, no mercado, &nbsp;em plena atividade h&aacute; mais de 50 anos,&nbsp;operando com a mesma lisura, respeito aos seus clientes e equil&iacute;brio nas suas decis&otilde;es.<br />\r\nTeve como fundador o Sr. Juarez Coutinho, que durante muitos anos esteve &agrave; frente dos neg&oacute;cios e na continuidade assumiram a dire&ccedil;&atilde;o, seus filhos: S&eacute;rgio de Moura Coutinho e Beatriz Coutinho do Amaral.&nbsp;<br />\r\nSua s&eacute;de (MATRIZ)&nbsp;est&aacute; localizada na Av. Brasil, 1000 e conta com mais cinco (05) filiais, em pontos estrat&eacute;gicos da cidade, para oferecer aos clientes: conforto, comodidade, produtos e servi&ccedil;os da melhor qualidade, visando justificar o slogan:&nbsp;<strong>QUEM CONSTR&Oacute;I...VAI&nbsp;&Aacute; PAVAN !!!</strong><br />\r\n<strong>VIS&Atilde;O</strong><br />\r\nSer refer&ecirc;ncia no mercado, local e regi&atilde;o, de materiais de constru&ccedil;&atilde;o, aprimorando e inovando sempre...<br />\r\n<strong>MISS&Atilde;O</strong><br />\r\nAtender plenamente e superar expectativas de seus clientes, com oferta de produtos de qualidade e atendimento diferenciado e personalizado.<br />\r\n<strong>PRINC&Iacute;PIOS E VALORES</strong><br />\r\nSuperar desafios;<br />\r\nRespeito&nbsp;&agrave;s pessoas e ao meio ambiente;&nbsp;<br />\r\n&Eacute;tica e Integridade;<br />\r\nTranspar&ecirc;ncia nas negocia&ccedil;&otilde;es;<br />\r\nInova&ccedil;&atilde;o;<br />\r\nCrescimento com sustentabilidade.', 1, 1, 'Y', '2015-09-03 11:48:14', '2015-09-03 11:48:14', 'N', 1),
